import {motion} from 'framer-motion';
import React from 'react';
import {NavLink} from 'react-router-dom';
import styled from 'styled-components';
import {Facebook, Github, LinkedIn, YouTube} from '../components/AllSvgs';
import {DarkTheme} from '../components/Themes';

const Icons = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;

  position: fixed;
  bottom: 0;
  left: 2rem;

  z-index: 3;

  & > *:not(:last-child) {
    margin: 0.5rem 0;
  }
`;

const Line = styled(motion.span)`
  width: 2px;
  height: 8rem;
  background-color: ${(props) =>
    props.color === 'dark' ? DarkTheme.text : DarkTheme.body};
`;

const SocialIcons = (props) => {
  return (
    <Icons>
      <motion.div
        initial={{transform: 'scale(0)'}}
        animate={{scale: [0, 1, 1.5, 1]}}
        transition={{type: 'spring', duration: 1, delay: 1}}
      >
        <NavLink
          style={{color: 'inherit'}}
          target="_blank"
          to={{pathname: 'https://github.com/macyso12'}}
        >
          <Github
            width={25}
            height={25}
            fill={props.theme === 'dark' ? DarkTheme.text : DarkTheme.body}
          />
        </NavLink>
      </motion.div>
      <motion.div
        initial={{transform: 'scale(0)'}}
        animate={{scale: [0, 1, 1.5, 1]}}
        transition={{type: 'spring', duration: 1, delay: 1.2}}
      >
        <NavLink
          style={{color: 'inherit'}}
          target="_blank"
          to={{pathname: 'https://www.linkedin.com/in/macyso/'}}
        >
          <LinkedIn
            width={25}
            height={25}
            fill={props.theme === 'dark' ? DarkTheme.text : DarkTheme.body}
          />
        </NavLink>
      </motion.div>
      <motion.div
        initial={{transform: 'scale(0)'}}
        animate={{scale: [0, 1, 1.5, 1]}}
        transition={{type: 'spring', duration: 1, delay: 1.4}}
      >
        <NavLink
          style={{color: 'inherit'}}
          target="_blank"
          to={{pathname: 'https://www.facebook.com/so.may.see.macy/'}}
        >
          <Facebook
            width={25}
            height={25}
            fill={props.theme === 'dark' ? DarkTheme.text : DarkTheme.body}
          />
        </NavLink>
      </motion.div>
      <motion.div
        initial={{transform: 'scale(0)'}}
        animate={{scale: [0, 1, 1.5, 1]}}
        transition={{type: 'spring', duration: 1, delay: 1.6}}
      >
        <NavLink
          style={{color: 'inherit'}}
          target="_blank"
          to={{
            pathname:
              'https://www.youtube.com/channel/UCJqH0ZPbDVJI4NJxDdw3Swg',
          }}
        >
          <YouTube
            width={25}
            height={25}
            fill={props.theme === 'dark' ? DarkTheme.text : DarkTheme.body}
          />
        </NavLink>
      </motion.div>

      <Line
        color={props.theme}
        initial={{
          height: 0,
        }}
        animate={{
          height: '8rem',
        }}
        transition={{
          type: 'spring',
          duration: 1,
          delay: 0.8,
        }}
      />
    </Icons>
  );
};

export default SocialIcons;
